mapper.select:
select

	from 

	where 1=1

	and GROUP_ID=#{pd.GROUP_ID}


		and
			(
				USERNAME  LIKE '%'+#{pd.keywords}+'%'

			)


		and CZTIME >= #{pd.lastStart} 


		and CZTIME <= #{pd.lastEnd} 


		and TABLE_NAME = #{pd.TABLE_NAME} 

	 and ZT='0'
	[fhstart]order by CZTIME desc[fhend]

mapper.update:
	update  set
	  ZT=#{newZT},
	  APPROVER_NAME = #{ApproverName},
	  APPROVE_TIME = #{ApproveTime}
     where
     ID=#{ID}
