mapper.update:
update

set 

    STATUS = #{STATUS}


    STOP_UPDATE = #{STOP_UPDATE}

where 
FHFILE_ID = #{FHFILE_ID}

mapper.select:
		select

		from 

		where 1=1

			and
				(
					NAME LIKE '%'+#{pd.keywords}+'%'
				)


            and CUSTOMER=#{pd.CUSTOMER} 


            and DEFECT_MODE=#{pd.DEFECT_MODE} 


            and PROCESS=#{pd.PROCESS} 


            and LEVEL=#{pd.LEVEL} 


			and DEPARTMENT_ID = ${pd.item}

		  [fhstart]order by CTIME desc[fhend]
